import TextBox from "../components/TextBox/TextBox";
import FilterBar from "../components/FilterBar/FilterBar";
import ComicCard from "../components/ComicCard/ComicCard";
import { useState, useEffect } from "react";

import { fetchComics, fetchCharacters, fetchCreators } from "../utils/utils";
const IMG_FANTASTIC = "portrait_fantastic";



interface comics {
  id: number;
  title: string;
  issuseNumber: number;
  creators: object;
  name: string;
  thumbnail: any;
  items: any;
};

interface creators {
  id: number;
  items: any;
  name: string;
  title: string;
  issuseNumber: number;
  creators: object;
  thumbnail: any;
}


export default function Home() {
    const [comics, setComics] = useState<comics[]>([]);
    const [creators, setCreators] = useState<creators[]>([]);
    const [error, setError] = useState();
  
    let cards: any;
    let creatorSection: any;

    const handleClick = async (e: any, args:any) => {
      e.preventDefault();
      if (args === "") return;
  
      try {
        return await fetchComics();
      } catch (err) {
        return err;
      }
    };
  
    if (comics) {
      cards = comics.map((comic: any) => (
        <ComicCard
          title={comic.title}
          issueNumber={comic.issueNumber}
          creators={comic.creators['items'][0]}
          key={comic.id}
          id={comic.id}
          thumbnail={`${comic.thumbnail.path}/${IMG_FANTASTIC}.${comic.thumbnail.extension}`}
        />
      ));
      console.log(cards.creators[0])
    }

  return (
    <div>
        <TextBox />
        <FilterBar
            handleClick={handleClick}
            setComics={setComics}
            setCreators={setCreators}
            setError={setError}
        />
        <div className="container">
          {cards ? cards : null}
          {creatorSection ? creatorSection : null}
        </div>
        <div>
    </div>
    </div>
  );
}
