import "./App.css";
import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

import NavBar from "../src/components/NavBar/NavBar";
import Footer from "../src/components/Footer/Footer";
import ComicCard from "./components/ComicCard/ComicCard";
import Favorites from "./routes/Favorites";
import Home from "./routes/Home";

interface comicProps {
  id: number;
  title: string;
  issuseNumber: number;
  creators: object;
  name: string;
  thumbnail: any;
  items: any;
};

// type creatorProps = {
//   id: number;
//   items: any;
//   name: string;
// };

export default function App() {

  const [comics, setComics] = useState<Array<comicProps>>([]);
  // const [creators, setCreators] = useState<Array<creatorProps>>([]);
  console.log(comics, "Comic Data");
  // console.log(creators, "Creator Data");

  useEffect(() => {
    const api = async () => {
      const data = await fetch(
        "https://gateway.marvel.com:443/v1/public/comics?limit=30&ts=1&apikey=a47015b472fa01cf91d12e737ddda35a&hash=bd67ce0fc9068238c3aac6ea8ca58732",
        {
          method: "GET"
        }
      );
      const jsonData = await data.json();
      // console.log(jsonData);
      setComics(jsonData.data.results);
      // setCreators(jsonData.data.results.creators);
      let dataObj: any = {};

      const creatorInfo: any = comics.length &&
        comics.map((comic) =>  {
          return (comic.creators);
      });
      console.log(creatorInfo, 'one')

      const creatorInfoName: any = Object.keys(creatorInfo).map(function (key) {
        return { items: creatorInfo[key].items };
      });
      console.log(creatorInfoName, 'two')

      const creatorInfoNameString: any = creatorInfoName.forEach((creatorInfoName: any) => {
        console.log(creatorInfoName.items);
      });
      console.log(creatorInfoNameString, 'three')

      const creatorInfoNameStringAll: any = Object.keys(creatorInfoNameString).map(function (key) {
        return { name: creatorInfoNameString[key] };
      });
      console.log(creatorInfoNameStringAll, 'four')

    };

    api();
  }, []);
  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path={"favorites"} element={<Favorites/>}/>
          <Route path={"/"} element={<Home/>}/>
        </Routes>
      <NavBar />
      <Footer />
      </Router>
    </div>
  );
}
