import "../../components/Dropdown/Dropdown.css";
import { useState } from "react";

export default function Dropdown(props:{ characters: any }) {
  const [selectedOption, setSelectedOption] = useState<String>();

  // This function is triggered when the select changes
  const selectChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const value = event.target.value;
    setSelectedOption(value);
  };

      return (
        <>
        <select onChange={selectChange} name="selectListCharacters" id="selectListCharacters">
          {props.characters.map((character: any) => {
            return <option key={character.id} value={character.id}>{character.name}</option>;
        })}
       </select>
       {selectedOption && <h2>{selectedOption}</h2>}
