import React, { useRef } from "react";
import Button from "../Button"
import Dropdown from "../Dropdown/Dropdown"

export default function FilterBar(props:{handleClick: any, setError: any, setComics: any, characters: any; selectChange: any; selectedOption: any; }) {
    return (
      <>
      <Dropdown 
        selectChange={(event: any) => {
          props.selectChange(event)
            .then((data: any) => props.setComics(data.data.results))
            .catch((err: any) => props.setError(err));
          }}
      />
      <form>
      <Button
        handleClick={(e: any) => {
          props.handleClick(e)
            .then((data: any) => props.setComics(data.data.results))
            .catch((err: any) => props.setError(err));
          }}
      />
    </form>
      </>
      );
  }
