//   name: string;
//   thumbnail: any;
// }

// interface creators {
//   id: number;
//   items: any;
//   name: string;
//   title: string;
//   issuseNumber: number;
//   creators: object;
//   thumbnail: any;
//   fullname: string;
// }


export default function Home() {
    const [comics, setComics] = useState<comics[]>([]);
    const [error, setError] = useState();
  
    let cards: any;
    let filter: any;

    const handleClick = async (e: any, args:any) => {
      e.preventDefault();
      if (args === "") return;
  
      try {
        return await fetchComics();
      } catch (err) {
        return err;
      }
    };
  
    if (comics) {
      cards = comics.map((comic: any) => (
        <ComicCard
          title={comic.title}
          key={comic.id}
          id={comic.id}
          issueNumber={comic.issueNumber}
          creators={comic.creators.items.map((creator: any) => {
            return creator.name;
          })}
          characters={comic.characters.items.map((character: any) => {
            return character.name;
          })}
          thumbnail={`${comic.thumbnail.path}/${IMG_FANTASTIC}.${comic.thumbnail.extension}`}
          />
        ));
        console.log(cards)
     }


  return (
    <>
        <TextBox />
        <Dropdown
          placeHolder="Select..." 
          />
        <div className="container">
          {cards ? cards : null}
        </div>
        <div>
    </div>
    </>
  );
}
