import "./App.css";
import { useState, useEffect } from "react";

import NavBar from "../src/components/NavBar/NavBar";
import HeroImage from "../src/components/HeroImage/HeroImage";
import TextBox from "../src/components/TextBox/TextBox";
import Footer from "../src/components/Footer/Footer";
import ComicCard from "./components/ComicCard/ComicCard";

type comicProps = {
  id: number;
  title: string;
  issuseNumber: number;
  creators: object;
};

type creatorProps = {
  id: number;
  items: object;
  name: string;
};

export default function App() {
  const [comics, setComics] = useState<Array<comicProps>>([]);
  const [creators, setCreators] = useState<Array<creatorProps>>([]);
  console.log(comics, "Comic Data");
  console.log(creators, "Creator Data");

  useEffect(() => {
    const api = async () => {
      const data = await fetch(
        "https://gateway.marvel.com:443/v1/public/comics?limit=30&ts=1&apikey=a47015b472fa01cf91d12e737ddda35a&hash=bd67ce0fc9068238c3aac6ea8ca58732",
        {
          method: "GET"
        }
      );
      const jsonData = await data.json();
      console.log(jsonData);
      setComics(jsonData.data.results);
      setCreators(jsonData.data.results.creators);
    };

    api();
  }, []);

  return (
    <div className="App">
      <NavBar />
      <HeroImage />
      <TextBox />
      <div className="comic-container">
        {comics.length > 0 &&
          comics.map((comic) =>  {
            return <ComicCard key={comic.id} comic={comic} />;
          })}
      </div>
      <div className="creator-container">
      {
      creators.map(creator => (
                <div key={creator.name}>
                    <p>{creator.name}</p>
                </div>
            ))}
      </div>
      <Footer />
    </div>
  );
}
