import TextBox from "../components/TextBox/TextBox";
import FilterBar from "../components/FilterBar/FilterBar";
import ComicCard from "../components/ComicCard/ComicCard";
import { useState, useEffect } from "react";

import { fetchComics, fetchCharacters, fetchCreators } from "../utils/utils";
const IMG_FANTASTIC = "portrait_fantastic";

interface characters {
    name: string;
    id: number;
}


interface comics {
    id: number;
    title: string;
    issuseNumber: number;
    creators: object;
    name: string;
    thumbnail: any;
    items: any;
  };


export default function Home() {
    const [comics, setComics] = useState<comics[]>([]);
    const [characters, setCharacters] = useState<characters[]>([]);
    const [error, setError] = useState();
  
    let cards: any;
  
    const handleClick = async (e: any, args:any) => {
      e.preventDefault();
      if (args === "") return;
  
      try {
        return await fetchComics();
      } catch (err) {
        return err;
      }
    };
  
    if (comics) {
      cards = comics.map((comic: any) => (
        <ComicCard
          title={comic.title}
          issueNumber={comic.issueNumber}
          creators={comic.creators}
          items={comic.creators.items}
          key={comic.id}
          id={comic.id}
          thumbnail={`${comic.thumbnail.path}/${IMG_FANTASTIC}.${comic.thumbnail.extension}`}
        />
      ));
    }
  return (
    <div>
        <TextBox />
        <FilterBar
            handleClick={handleClick}
            setComics={setComics}
            setError={setError}
        />
        <div className="container">{cards ? cards : null}</div>
    </div>
  );
}
