import TextBox from "../components/TextBox/TextBox";
import FilterBar from "../components/FilterBar/FilterBar";
import ComicCard from "../components/ComicCard/ComicCard";
import { useState, useEffect } from "react";

import { fetchCharacters, fetchCreators } from "../utils/utils";
const IMG_FANTASTIC = "portrait_fantastic";

interface creators {
    fullName: string;
}


export default function Home() {
    const [creators, setCreators] = useState<creators>([]);
    // const [error, setError] = useState();
  
    let cards: any;
  
    const handleClick = async (e, args) => {
      e.preventDefault();
      if (args === "") return;
  
      try {
        return await fetchCreators(args);
      } catch (err) {
        return err;
      }
    };
  
    if (creators) {
      cards = creators.map((creator: any) => (
        <ComicCard
          name={creator.fullName}
          key={creator.id}
          thumbnail={`${creator.thumbnail.path}/${IMG_FANTASTIC}.${creator.thumbnail.extension}`}
        />
      ));
    }
  return (
    <div>
        <TextBox />
        <FilterBar
            handleClick={handleClick}
            setHeroes={setCreators}
        />
    </div>
  );
}
