import "../../components/Dropdown/Dropdown.css";
import { useState } from "react";
import Button from "../Button"

export default function Dropdown(props:{ characters: any, selectChange: any, setError: any, setComics: any, handleClick: any, selectedOption: any }) {

      return (
        <div className="dropdown-container">
        <select onChange={props.selectChange} name="selectListCharacters" id="selectListCharacters">
          {props.characters.map((character: any) => {
            return <option key={character.id} value={character.name}>{character.name}</option>;
        })}
       </select>
       {props.selectedOption && <h2>{props.selectedOption }</h2>}
        {/* <select name="selectListCreators" id="selectListCreators">
          {props.creators.map((creator: any) => {
            return <option key={creator.id} value={creator.id}>{creator.name}</option>;
        })}
       </select> */}
               <form>
        <Button
          handleClick={(e: any) => {
            props.handleClick(e)
              .then((data: any) => props.setComics(data.data.results))
              .catch((err: any) => props.setError(err));
            }}
        />
      </form>
        </div>
      );
    };
