import React, { useRef } from "react";
import Button from "../Button"

export default function FilterBar(props:{handleClick: any, setComics: any, setError: any, setCharacters: any}) {
    let input = useRef(null);
    return (
        <form>
        <input type="text" placeholder="Search hero..." ref={input} />
        <Button
          text={"Search"}
          handleClick={(e: any) => {
            props.handleClick(e, input)
              .then((data: any) => props.setComics(data.data.results))
              .then((data: any) => props.setCharacters(data.data.results[4].creators.items[0].name))
              .catch((err: any) => props.setError(err));
          }}
        />
      </form>
      );
  }
