import TextBox from "../components/TextBox/TextBox";
import FilterBar from "../components/FilterBar/FilterBar";
import Dropdown from "../components/Dropdown/Dropdown";
import ComicCard from "../components/ComicCard/ComicCard";
import { useState, useEffect } from "react";

import { fetchComics, fetchCharacters } from "../utils/utils";
const IMG_FANTASTIC = "portrait_fantastic";



type comics = {
  id: number;
  title: string;
  issuseNumber: number;
  creators: object;
  characters: object;
  name: string;
  thumbnail: any;
  items: any;
};



export default function Home() {
    const [comics, setComics] = useState<comics[]>([]);
    const [error, setError] = useState();
    const [selectedOption, setSelectedOption] = useState<String>();
    
    const characters = [
      {name : "Crusher Hogan (Ultimate)", id: 1014991},
      {name : "Apocalypse", id: 1009156},
      {name : "Blink", id: 1009197},
      {name : "Blob", id: 1009199},
      {name : "Storm", id: 1009629},
      {name : "Magento", id: 1009417},
    ]
    
    // This function is triggered when the select changes
    const selectChange = async (event: React.ChangeEvent<HTMLSelectElement>, args: any) => {
      const value = event.target.value;
      setSelectedOption(value);
      event.preventDefault();
      if (args === "") return;
  
      try {
        return await fetchComics();
      } catch (err) {
        return err;
      }
    };


    const handleClick = async (e: any, args:any) => {
      e.preventDefault();
      if (args === "") return;
  
      try {
        return await fetchComics();
      } catch (err) {
        return err;
      }
    };


    // const selectChange = async (e: any, args:any) => {
    //   e.preventDefault();
    //   if (args === "") return;
  
    //   try {
    //     return await fetchCharacters();
    //   } catch (err) {
    //     return err;
    //   }
    // };


  return (
    <>
        <TextBox />
        <FilterBar handleClick={handleClick} setComics={setComics} setError={setError} selectChange={selectChange} characters={characters} />
        {/* <Dropdown characters={characters} selectChange={selectChange} selectedOption={selectedOption} />  */}
        <div className="container">
        {comics.map((comic: any) => (
          <ComicCard
            title={comic.title}
            key={comic.id}
            id={comic.id}
            issueNumber={comic.issueNumber}
            creators={comic.creators.items.map((creator: any) => {
              return creator.name;
            })}
            thumbnail={`${comic.thumbnail.path}/${IMG_FANTASTIC}.${comic.thumbnail.extension}`}
            />
            ))}
        </div>
    </>
  );
}
