import TextBox from "../components/TextBox/TextBox";
import FilterBar from "../components/FilterBar/FilterBar";
import Dropdown from "../components/Dropdown/Dropdown";
import ComicCard from "../components/ComicCard/ComicCard";
import { useState, useEffect } from "react";

import { fetchCharacters, fetchCreators } from "../utils/utils";
const IMG_FANTASTIC = "portrait_fantastic";



// interface comics {
//   id: number;
//   title: string;
//   issuseNumber: number;
//   creators: object;
//   name: string;
//   thumbnail: any;
//   items: any;
// };

interface characters {
  id: number;
  name: string;
  thumbnail: any;
}

interface creators {
  id: number;
  items: any;
  name: string;
  title: string;
  issuseNumber: number;
  creators: object;
  thumbnail: any;
  fullname: string;
}


export default function Home() {
    // const [comics, setComics] = useState<comics[]>([]);
    const [characters, setCharacters] = useState<characters[]>([]);
    const [creators, setCreators] = useState<creators[]>([]);
    const [error, setError] = useState();
  
    let cards: any;

    const handleClick = async (e: any, args:any) => {
      e.preventDefault();
      if (args === "") return;
  
      try {
        return await fetchCharacters() && fetchCreators();
      } catch (err) {
        return err;
      }
    };
  
    if (characters && creators) {
      cards = characters.map((character: any) => creators.map((creator: any) => (
        <ComicCard
          name={character.name}
          key={character.id}
          id={character.id}
          fullName={creator.fullName}
          thumbnail={`${character.thumbnail.path}/${IMG_FANTASTIC}.${character.thumbnail.extension}`}
        />
      )));
    }

    // const handleClickCreators = async (e: any, args:any) => {
    //   e.preventDefault();
    //   if (args === "") return;
  
    //   try {
    //     return await fetchCreators();
    //   } catch (err) {
    //     return err;
    //   }
    // };
  
    // if (creators) {
    //   cards = characters.map((creator: any) => (
    //     <ComicCard
    //       name={creator.name}
    //       fullName={creator.fullName}
    //       key={creator.id}
    //       id={creator.id}
    //       thumbnail={`${creator.thumbnail.path}/${IMG_FANTASTIC}.${creator.thumbnail.extension}`}
    //     />
    //   ));
    //   console.log(cards)
    // }

  return (
    <div>
        <TextBox />
        <FilterBar
            handleClick={handleClick}
            // handleClickCreators={handleClickCreators}
            setCharacters={setCharacters}
            setCreators={setCreators}
            setError={setError}
            // characters={characters}
        />
        <div className="container">
          {cards ? cards : null}
        </div>
        <div>
    </div>
    </div>
  );
}
