import React, { useRef } from "react";
import Button from "../Button"
import Dropdown from "../Dropdown/Dropdown"

export default function FilterBar(props:{handleClick: any, setError: any, setComics: any, selectChange: any, characters: any, selectedOption: any }) {
    return (
      <>
      {/* <Dropdown /> */}
        {/* <select onChange={props.selectChange} name="selectListCharacters" id="selectListCharacters">
          {props.characters.map((character: any) => {
           return <option key={character.id} value={character.name}>{character.name}</option>;
        })}
       </select> */}
       {props.selectedOption && <div>{props.selectedOption}</div>}
      <form>
      <Button
        handleClick={(e: any) => {
          props.handleClick(e)
            .then((data: any) => props.setComics(data.data.results))
            .catch((err: any) => props.setError(err));
          }}
      />
    </form>
      </>
      );
  }
