import { useState, useEffect } from "react";
import "../ComicCard/ComicCard.css";

interface creators {
  id: number;
  items: any;
  name: string;
  title: string;
  issuseNumber: number;
  creators: object;
  thumbnail: any;
  fullname: string;
}

export default function ComicCard(props:{id: number, thumbnail: any, title: string, creators: any, issueNumber: number }) {

  return (
    <div className="comic-card">
      <p className="card-title">{props.title}</p>
      <span>Issue:</span>
      <p className="card-issue">{props.issueNumber}</p>
      <p className="publish-date">Published: August 31, 2022</p>
      <span>Creators:</span>
      <div className="card-creators">
      {items.map((creator: any) => {
            return <li key={creator.id} value={creator.id}>{creator.name}</li>
      })}
      </div>     
      <img src={props.thumbnail} alt="thumbnail" />
    </div>
  );
}
