import { useState, useEffect } from "react";
import "../ComicCard/ComicCard.css";

export default function ComicCard({ comic, creator }: any) {

  return (
    <div className="comic-card">
      <p className="card-title">{comic.title}</p>
      <span>Issue:</span>
      <p className="card-issue">{comic.issueNumber}</p>
      <p className="publish-date">Published: August 31, 2022</p>
      <span>Creators:</span>
      <p className="card-creators">{comic.creators.items.name}</p>
      <div className="comic-container">
        {comic.length > 0 &&
          comic.map((creator) =>  {
            return <div key={creator.id}>{creator.item.name}</div>;
          })}
      </div>
    </div>
  );
}
