import { useState, useEffect } from "react";
import "../ComicCard/ComicCard.css";

interface creators {
  id: number;
  items: any;
  name: string;
  title: string;
  issuseNumber: number;
  creators: object;
  thumbnail: any;
  fullname: string;
}

export default function ComicCard(props:{id: number, thumbnail: any, title: string, creators: any }) {
  
  const [creators, setCreators] = useState<creators[]>([]);

  // let cards: any;

  // if (creators) {
  //   cards = creators.map((creator: any) => (
  //     <ComicCard
  //       title={creator.name}
  //       key={creator.id}
  //       id={creator.id}
  //     />
  //   ));
  // }

  return (
    <div className="comic-card">
      <p className="card-title">{props.title}</p>
      <span>Issue:</span>
      {/* <p className="card-issue">{props.issueNumber}</p> */}
      <p className="publish-date">Published: August 31, 2022</p>
      <span>Creators:</span>
      <div>
      {creators.map(creator => {
            return <li key={creator.id} value={creator.id}>{creator.name}</li>
        })}
      </div>
      <p className="card-creators">{props.creators.name}</p>        
      <img src={props.thumbnail} alt="thumbnail" />
    </div>
  );
}
