import TextBox from "../components/TextBox/TextBox";
import FilterBar from "../components/FilterBar/FilterBar";
import Dropdown from "../components/Dropdown/Dropdown";
import ComicCard from "../components/ComicCard/ComicCard";
import { useState, useEffect } from "react";

import { fetchComics, fetchCharacters } from "../utils/utils";
const IMG_FANTASTIC = "portrait_fantastic";



type comics = {
  id: number;
  title: string;
  issuseNumber: number;
  creators: object;
  characters: object;
  name: string;
  thumbnail: any;
  items: any;
};

type characters = {
  id: number;
  name: string;
};


export default function Home() {
    const [comics, setComics] = useState<comics[]>([]);
    const [characters, setCharacters] = useState<characters[]>([]);
    const [error, setError] = useState();
  
  //   const characters = [
  //     {name : "Iron Man", id: 1009368},
  //     {name : "Captain America", id: 1009220},
  //     {name : "Thor", id: 1009664},
  //     {name : "Deadpool", id: 1009268},
  //     {name : "Scarlet Witch", id: 1009562},
  //     {name : "Black Widow", id: 1009189},
  //     {name : "Wasp", id: 1009707},
  //     {name : "Gamora", id: 1010763}
  // ]
  // const creators = [
  //     {name : "Kate Leth", id : 12787},
  //     {name : "Brian Michael Bendis", id : 24},
  //     {name : "Stan Lee", id : 30},
  //     {name : "Steve Ditko", id : 32},
  //     {name : "Jack Kirby", id : 196}
  // ]

    const handleClick = async (e: any, args:any) => {
      e.preventDefault();
      if (args === "") return;
  
      try {
        return await fetchComics();
      } catch (err) {
        return err;
      }
    };


  return (
    <>
        <TextBox />
        <FilterBar handleClick={handleClick} setComics={setComics} setError={setError} />
        <Dropdown characters={characters}  /> 
        <div className="container">
        {comics.map((comic: any) => (
          <ComicCard
            title={comic.title}
            key={comic.id}
            id={comic.id}
            issueNumber={comic.issueNumber}
            creators={comic.creators.items.map((creator: any) => {
              return creator.name;
            })}
            thumbnail={`${comic.thumbnail.path}/${IMG_FANTASTIC}.${comic.thumbnail.extension}`}
            />
            ))}
        </div>
    </>
  );
}
